'use strict';

const discord_js = require('discord.js');

function createWebhookClient(info, options) {
  try {
    return new discord_js.WebhookClient(
      typeof info === "string" ? { url: info } : info,
      options
    );
  } catch {
    return null;
  }
}

exports.createWebhookClient = createWebhookClient;
